
# https://leetcode.com/problems/find-substring-with-given-hash-value/


class Solution:
    def subStrHash(
        self, s: str, power: int, modulo: int, k: int, hashValue: int
    ) -> str:

        for i in range(0, len(s)):
            sub = s[i : i + k]
            out_hash = self.get_hash(sub, power, modulo)
            if out_hash == hashValue:
                return sub

    def get_hash(self, sub, power, modulo):
        total = 0
        for idx, letter in enumerate(sub):
            num_letter = ord(letter) - ord("a") + 1
            total += num_letter * (power**idx)
        out_hash = total % modulo

        return out_hash


def test_01():
    s = "leetcode"
    power = 7
    module = 20
    k = 2
    hash_value = 0

    # this is the answer
    sub = "ee"

    sol = Solution()
    assert sol.subStrHash(s, power, module, k, hash_value) == sub


